import React, { useState, useRef, useEffect } from 'react';
import { Container, Card, Table, Spinner, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import './AdminDashboard.css'; // CSS file for styling

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [filteredLearners, setFilteredLearners] = useState([]);
  const reportRef = useRef();

  useEffect(() => {
    fetchLearnerData();
  }, []);

  const fetchLearnerData = () => {
    setLoading(true);
    fetch('https://sheetdb.io/api/v1/131pnl5khpeib')
      .then((res) => res.json())
      .then((data) => {
        const learner = data.find((learner) => learner.RollNo === '12345'); // Example RollNo
        setFilteredLearners(learner ? [learner] : []);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching learner data:', err);
        setLoading(false);
      });
  };

  const exportToPDF = () => {
    if (!reportRef.current) return;
    html2canvas(reportRef.current, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const pdfWidth = 210;
      const imgProps = pdf.getImageProperties(imgData);
      const imgHeight = (imgProps.height * pdfWidth) / imgProps.width;
      const marginTop = (297 - imgHeight) / 2;
      pdf.addImage(imgData, 'PNG', 0, Math.max(marginTop, 0), pdfWidth, imgHeight);
      pdf.save(`Report.pdf`);
    });
  };

  return (
    <Container className="mt-5 text-center"> 
      <Card className="p-4 shadow-sm">
        {loading ? (
          <div className="text-center">
            <Spinner animation="border" variant="primary" />
          </div>
        ) : filteredLearners.length > 0 ? (
          <div className="report-container">
            <div ref={reportRef} className="report-print-wrapper">
              <div className="text-center mb-4">
                <img src="https://v.fastcdn.co/u/67ec1086/61513884-0-Unacademy-Logo-RGB.png" alt="Unacademy Logo" style={{ height: '60px' }} />
                <h2 className="mt-3">AHMEDABAD CENTRE</h2>
              </div>

              {filteredLearners.map((learner, idx) => (
                <div key={idx} className="mb-5">
                  <h5 className="mb-3">Learner Information</h5>
                  <Table bordered hover responsive size="sm" className="text-center table-bordered table-striped">
                    <thead className="table-primary">
                      <tr>
                        <th>Learner Name</th>
                        <th>Batch Name</th>
                        <th>Roll No</th>
                        <th>Contact No</th>
                        <th>Session</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>{learner["Student Name"]}</td>
                        <td>{learner.Batch}</td>
                        <td>{learner.RollNo}</td>
                        <td>{learner["Contact No"]}</td>
                        <td>2025-26</td>
                      </tr>
                    </tbody>
                  </Table>

                  <h5 className="mb-3">Attendance Report 2025-26</h5>
                  <Table bordered hover responsive size="sm" className="text-center table-bordered">
                    <thead className="table-success">
                      <tr>
                        <th>APR</th><th>MAY</th><th>JUN</th><th>JUL</th>
                        <th>AUG</th><th>SEP</th><th>OCT</th><th>NOV</th>
                        <th>DEC</th><th>JAN</th><th>FEB</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>{learner.APR}</td>
                        <td>{learner.MAY}</td>
