import React, { useEffect, useState, useRef } from 'react';
import { Container, Card, Button, Table, Form, Spinner, Row, Col } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import './AdminDashboard.css'; // CSS file for styling

const AdminDashboard = () => {
  const navigate = useNavigate();
  // This state is not currently used, consider removing if not needed.
  // const [learners, setLearners] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchRollNo, setSearchRollNo] = useState('');
  const [customRollNos, setCustomRollNos] = useState(''); // New state for comma-separated roll numbers
  const [filteredLearners, setFilteredLearners] = useState([]);
  const [theme, setTheme] = useState('light');
  const reportRef = useRef();

  // Function to handle the search for a specific roll number
  const handleSearch = () => {
    if (!searchRollNo) {
      setFilteredLearners([]); // Clear previous results if search is empty
      return;
    }
    setLoading(true); // Start loading indicator
    // Fetch data from the SheetDB API
    fetch('https://sheetdb.io/api/v1/131pnl5khpeib')
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        // Filter learners based on the entered Roll No
        const filtered = data.filter(
          (learner) => learner.RollNo && String(learner.RollNo).trim() === searchRollNo.trim()
        );
        setFilteredLearners(filtered); // Update state with filtered learners
        setLoading(false); // Stop loading indicator
      })
      .catch((err) => {
        console.error('Error fetching learner data:', err);
        setFilteredLearners([]); // Clear results on error
        setLoading(false); // Stop loading indicator
      });
  };

  // Function to export the current displayed report to PDF
  const exportToPDF = () => {
    // Ensure there is content to export
    if (!reportRef.current) {
      console.error("Report reference is not available for PDF export.");
      return;
    }

    // Use html2canvas to render the report section as a canvas image
    // useCORS: true is added to handle external images like the logo
    html2canvas(reportRef.current, { scale: 2, useCORS: true }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png'); // Get image data as PNG
      const pdf = new jsPDF('p', 'mm', 'a4'); // Initialize jsPDF with A4 portrait size
      const pdfWidth = 210; // A4 width in mm
      const pdfHeight = 297; // A4 height in mm

      // Calculate image dimensions to fit within PDF, maintaining aspect ratio
      const imgProps = pdf.getImageProperties(imgData);
      const imgHeight = (imgProps.height * pdfWidth) / imgProps.width;

      // Calculate vertical margin to center the image if it's shorter than the page
      const marginTop = (pdfHeight - imgHeight) / 2;

      // Add the image to the PDF
      // The x-coordinate is set to 0, assuming the html2canvas output is already centered or stretched.
      // The CSS is adjusted to ensure content is centered within the captured area.
      pdf.addImage(imgData, 'PNG', 0, Math.max(marginTop, 0), pdfWidth, imgHeight);
      pdf.save(`Report_${searchRollNo || 'current'}.pdf`); // Save the PDF with a dynamic filename
    }).catch(error => {
      console.error("Error generating PDF with html2canvas:", error);
    });
  };

  // Function to export all unique learners' reports as separate PDFs
  const exportAllLearners = async () => {
    setLoading(true); // Start loading indicator for bulk export
    try {
      // Fetch all learner data
      const res = await fetch('https://sheetdb.io/api/v1/131pnl5khpeib');
      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      const data = await res.json();

      // Get unique roll numbers from the data, ensuring they are strings and not empty
      const uniqueRollNos = [...new Set(data.map(item => String(item.RollNo)).filter(Boolean))];

      // Loop through each unique roll number to generate a PDF
      for (const rollNo of uniqueRollNos) {
        // Filter data for the current roll number
        const learnerSet = data.filter(l => String(l.RollNo) === rollNo);
        if (learnerSet.length === 0) continue; // Skip if no data for this roll number

        // Temporarily set filteredLearners to the current learner's data
        // This is crucial for html2canvas to render the correct report
        setFilteredLearners(learnerSet);
        // Wait a short period to allow React to re-render the component with new data
        await new Promise(resolve => setTimeout(resolve, 500)); // Increased timeout for stability

        // Generate canvas from the report section with useCORS: true
        const canvas = await html2canvas(reportRef.current, { scale: 2, useCORS: true });
        const imgData = canvas.toDataURL('image/png'); // Get image data

        // Initialize jsPDF for A4 portrait
        const pdf = new jsPDF('p', 'mm', 'a4');
        const pdfWidth = 210; // A4 width in mm
        const pdfHeight = 297; // A4 height in mm

        // Calculate image dimensions and position
        const imgProps = pdf.getImageProperties(imgData);
        const imgHeight = (imgProps.height * pdfWidth) / imgProps.width;
        const marginTop = (pdfHeight - imgHeight) / 2;

        // Add image to PDF
        pdf.addImage(imgData, 'PNG', 0, Math.max(marginTop, 0), pdfWidth, imgHeight);
        pdf.save(`Report_${rollNo}.pdf`); // Save PDF
      }
      // Reset filtered learners after all exports
      setFilteredLearners([]);
    } catch (err) {
      console.error('Error during bulk export:', err);
    }
    setLoading(false); // Stop loading indicator
  };

  // Function to export reports for a list of comma-separated roll numbers as separate PDFs
  const exportSelectedLearnersPDFs = async () => {
    if (!customRollNos) {
      alert('Please enter roll numbers separated by commas.'); // Use an alert for simple user feedback
      return;
    }
    setLoading(true);
    try {
      const rollNumbersArray = customRollNos.split(',').map(rn => rn.trim()).filter(Boolean); // Parse input
      const res = await fetch('https://sheetdb.io/api/v1/131pnl5khpeib');
      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      const allData = await res.json();

      for (const rollNo of rollNumbersArray) {
        const learnerSet = allData.filter(l => String(l.RollNo) === rollNo);
        if (learnerSet.length === 0) {
          console.warn(`No data found for Roll No: ${rollNo}`);
          continue; // Skip if no data for this roll number
        }

        setFilteredLearners(learnerSet);
        await new Promise(resolve => setTimeout(resolve, 500)); // Wait for re-render

        const canvas = await html2canvas(reportRef.current, { scale: 2, useCORS: true });
        const imgData = canvas.toDataURL('image/png');

        const pdf = new jsPDF('p', 'mm', 'a4');
        const pdfWidth = 210;
        const pdfHeight = 297;
        const imgProps = pdf.getImageProperties(imgData);
        const imgHeight = (imgProps.height * pdfWidth) / imgProps.width;
        const marginTop = (pdfHeight - imgHeight) / 2;

        pdf.addImage(imgData, 'PNG', 0, Math.max(marginTop, 0), pdfWidth, imgHeight);
        pdf.save(`Report_${rollNo}.pdf`);
      }
      setFilteredLearners([]);
      setCustomRollNos(''); // Clear the input field
    } catch (err) {
      console.error('Error during selected learners export:', err);
    }
    setLoading(false);
  };

  // Function to export multiple learners into a single combined PDF
  const exportCombinedLearnersPDF = async () => {
    setLoading(true);
    try {
      const res = await fetch('https://sheetdb.io/api/v1/131pnl5khpeib');
      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      const allData = await res.json();
      // Use filteredLearners if a search has been performed, otherwise get all unique learners
      const learnersToExport = filteredLearners.length > 0
        ? [...new Set(filteredLearners.map(l => String(l.RollNo)).filter(Boolean))]
        : [...new Set(allData.map(item => String(item.RollNo)).filter(Boolean))];

      if (learnersToExport.length === 0) {
        alert('No learners to export. Please search or ensure data is available.');
        setLoading(false);
        return;
      }

      const pdf = new jsPDF('p', 'mm', 'a4'); // Initialize single PDF instance

      for (let i = 0; i < learnersToExport.length; i++) {
        const rollNo = learnersToExport[i];
        const learnerSet = allData.filter(l => String(l.RollNo) === rollNo);
        if (learnerSet.length === 0) {
          console.warn(`No data found for Roll No: ${rollNo} for combined PDF.`);
          continue;
        }

        setFilteredLearners(learnerSet);
        await new Promise(resolve => setTimeout(resolve, 500)); // Wait for re-render

        const canvas = await html2canvas(reportRef.current, { scale: 2, useCORS: true });
        const imgData = canvas.toDataURL('image/png');

        const pdfWidth = 210;
        const pdfHeight = 297;
        const imgProps = pdf.getImageProperties(imgData);
        const imgHeight = (imgProps.height * pdfWidth) / imgProps.width;
        const marginTop = (pdfHeight - imgHeight) / 2;

        if (i > 0) {
          pdf.addPage(); // Add a new page for each subsequent learner
        }
        pdf.addImage(imgData, 'PNG', 0, Math.max(marginTop, 0), pdfWidth, imgHeight);
      }
      pdf.save(`Combined_Learner_Reports.pdf`); // Save the combined PDF
      setFilteredLearners([]);
    } catch (err) {
      console.error('Error during combined PDF export:', err);
    }
    setLoading(false);
  };


  return (
    // Main container for the dashboard, applies theme class
    <Container className={`mt-5 text-center ${theme}`}>
      <Card className={`p-4 shadow-sm ${theme}`}>
        {/* Search form, hidden when printing */}
        <Form className="mb-4 print-only-hide">
          <Row className="justify-content-center mb-3">
            <Col xs={12} md={6} lg={4}>
              <Form.Control
                type="text"
                placeholder="Enter Single Roll Number"
                value={searchRollNo}
                onChange={(e) => setSearchRollNo(e.target.value)}
                className="text-center"
              />
            </Col>
            <Col xs={12} md={6} lg={2} className="mt-2 mt-md-0">
              <Button variant="primary" onClick={handleSearch} disabled={loading}>🔍 Search</Button>
            </Col>
          </Row>

          <Row className="justify-content-center">
            <Col xs={12} md={6} lg={6}>
              <Form.Control
                type="text"
                placeholder="Enter Roll Numbers (comma-separated for bulk export)"
                value={customRollNos}
                onChange={(e) => setCustomRollNos(e.target.value)}
                className="text-center"
              />
            </Col>
            <Col xs={12} md={6} lg={4} className="mt-2 mt-md-0">
              <Button variant="info" onClick={exportSelectedLearnersPDFs} disabled={loading} className="me-2">
                📂 Export Selected PDFs
              </Button>
              <Button variant="warning" onClick={exportCombinedLearnersPDF} disabled={loading}>
                🗂️ Export Combined PDF
              </Button>
            </Col>
          </Row>
        </Form>


        {/* Loading spinner or report content */}
        {loading ? (
          <div className="text-center">
            <Spinner animation="border" variant="primary" />
          </div>
        ) : filteredLearners.length > 0 ? (
          // Report container for display and PDF export
          <div className="report-container">
            <div ref={reportRef} className="report-print-wrapper">
              {/* Unacademy Logo and Ahmedabad Centre heading */}
              <div className="text-center mb-4">
                <img src="https://v.fastcdn.co/u/67ec1086/61513884-0-Unacademy-Logo-RGB.png" alt="Unacademy Logo" style={{ height: '60px' }} />
                <h2 className="mt-3">AHMEDABAD CENTRE</h2>
              </div>

              {/* Learner Information Table */}
              <h5 className="mb-3">Learner Information</h5>
              <Table bordered hover responsive size="sm" className="text-center table-bordered table-striped">
                <thead className="table-primary">
                  <tr>
                    <th>Learner Name</th>
                    <th>Batch Name</th>
                    <th>Roll No</th>
                    <th>Contact No</th>
                    <th>Session</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{filteredLearners[0] && filteredLearners[0]["Student Name"]}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].Batch}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].RollNo}</td>
                    <td>{filteredLearners[0] && filteredLearners[0]["Contact No"]}</td>
                    <td>2025-26</td> {/* Static session year */}
                  </tr>
                </tbody>
              </Table>

              {/* Attendance Report Table */}
              <h5 className="mb-3">Attendance Report 2025-26</h5>
              <Table bordered hover responsive size="sm" className="text-center table-bordered">
                <thead className="table-success">
                  <tr>
                    <th>APR</th><th>MAY</th><th>JUN</th><th>JUL</th>
                    <th>AUG</th><th>SEP</th><th>OCT</th><th>NOV</th>
                    <th>DEC</th><th>JAN</th><th>FEB</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{filteredLearners[0] && filteredLearners[0].APR}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].MAY}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].JUN}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].JUL}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].AUG}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].SEP}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].OCT}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].NOV}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].DEC}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].JAN}</td>
                    <td>{filteredLearners[0] && filteredLearners[0].FEB}</td>
                  </tr>
                </tbody>
              </Table>

              {/* Learner Performance Report Table */}
              <h5 className="mb-3">Learner Performance Report</h5>
              <Table bordered hover responsive size="sm" className="text-center table-bordered table-striped">
                <thead className="table-warning">
                  <tr>
                    <th>S.NO.</th>
                    <th>EXAM DATE</th>
                    <th>TEST NAME</th>
                    <th>PHY</th>
                    <th>CHEM</th>
                    <th>MATH</th>
                    <th>BIO</th>
                    <th>TOTAL</th>
                    <th>%AGE</th>
                    <th>RANK</th>
                    <th>MAX. MARKS</th>
                  </tr>
                </thead>
                <tbody>
                  {/* Map through filtered learners to display performance data */}
                  {filteredLearners.map((learner, idx) => (
                    <tr key={idx}>
                      <td>{idx + 1}</td>
                      <td>{learner["Exam Date"]}</td>
                      <td>{learner["TestName"]}</td>
                      <td>{learner["Phy"]}</td>
                      <td>{learner["Chem"]}</td>
                      <td>{learner["Math"]}</td>
                      <td>{learner["Bio"]}</td>
                      <td>{learner["Total"]}</td>
                      <td>{learner["%AGE"]}</td>
                      <td>{learner["Rank"]}</td>
                      <td>{learner["Max. Marks"]}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </div>
          </div>
        ) : (
          // Message when no data is found
          searchRollNo && <div className="text-center text-danger">No data found for Roll No: {searchRollNo}</div>
        )}

        {/* Action buttons, hidden when printing */}
        <div className="text-center mt-4 print-only-hide">
          <Button variant="outline-secondary" onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')}>
            {theme === 'light' ? '🌙 Dark Mode' : '🌞 Light Mode'}
          </Button>{' '}
          <Button variant="success" onClick={exportToPDF} disabled={loading}>📄 Export Current PDF</Button>{' '}
          <Button variant="primary" onClick={exportAllLearners} disabled={loading}>📁 Export All as PDFs</Button>{' '}
          <Button variant="info" onClick={() => window.print()} disabled={loading}>🖨️ Print Report</Button>{' '}
          <Button variant="secondary" onClick={() => navigate('/')} disabled={loading}>🔙 Back</Button>
        </div>
      </Card>
    </Container>
  );
};

export default AdminDashboard;
