import React, { useState } from 'react';
import { Form, Button, Spinner, Card, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './LoginForm.css';
import { useNavigate } from 'react-router-dom';

const LoginForm = () => {
  const [rollNo, setRollNo] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [showOverlay, setShowOverlay] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setShowOverlay(true);

    fetch('https://sheetdb.io/api/v1/131pnl5khpeib')
      .then((res) => res.json())
      .then((data) => {
        const learner = data.find(
          (l) => l.RollNo === rollNo.trim() && l.Password === password.trim()
        );
        if (learner) {
          navigate('/learnerPerformance', { state: { learnerData: [learner] } });
        } else {
          alert('Invalid Roll Number or Password');
        }
      })
      .catch((err) => {
        console.error('Error:', err);
        alert('Something went wrong');
      })
      .finally(() => {
        setLoading(false);
        setShowOverlay(false);
      });
  };

  return (
    <>
      {showOverlay && <div className="overlay" />}
      <Container className="login-container">
        <Card className="login-card">
          <Card.Body>
            <img
              src="https://v.fastcdn.co/u/67ec1086/61513884-0-Unacademy-Logo-RGB.png"
              alt="Unacademy"
              className="logo-image"
            />
            <h2 className="login-title">Ahmedabad Centre</h2>
            <h4 className="text-muted mb-4">Learner Login</h4>

            <Form onSubmit={handleSubmit}>
              <Form.Group>
                <Form.Label>Roll Number</Form.Label>
                <Form.Control
                  type="text"
                  value={rollNo}
                  onChange={(e) => setRollNo(e.target.value)}
                  placeholder="Enter Roll Number"
                  required
                />
              </Form.Group>

              <Form.Group className="mt-3">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter Password"
                  required
                />
              </Form.Group>

              <Button
                type="submit"
                className="submit-button mt-4"
                disabled={loading}
              >
                {loading ? (
                  <Spinner animation="border" size="sm" />
                ) : (
                  'Login'
                )}
              </Button>
            </Form>
          </Card.Body>
        </Card>
      </Container>
    </>
  );
};

export default LoginForm;
