import React, { useEffect, useState, useRef } from 'react';
import { Container, Card, Button, Table, Form, Spinner } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import './AdminDashboard.css'; // CSS file for styling

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [learners, setLearners] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchRollNo, setSearchRollNo] = useState('');
  const [filteredLearners, setFilteredLearners] = useState([]);
  const [theme, setTheme] = useState('light');
  const reportRef = useRef();

  const handleSearch = () => {
    if (!searchRollNo) return;
    setLoading(true);
    fetch('https://sheetdb.io/api/v1/131pnl5khpeib')
      .then((res) => res.json())
      .then((data) => {
        const filtered = data.filter(
          (learner) => learner.RollNo && learner.RollNo.trim() === searchRollNo.trim()
        );
        setFilteredLearners(filtered);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching learner data:', err);
        setLoading(false);
      });
  };

  const exportToPDF = () => {
    if (!reportRef.current) return;
    html2canvas(reportRef.current, { scale: 2 }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('l', 'mm', 'a4');
      const pdfWidth = 297; // A4 landscape width
      const pdfHeight = 210; // A4 landscape height
      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save(`Report_${searchRollNo}.pdf`);
    });
  };

  return (
    <Container className={`mt-5 ${theme}`}> 
      <Card className={`p-4 shadow-sm ${theme}`}>
        <div className="text-center mb-4">
          <img src="https://v.fastcdn.co/u/67ec1086/61513884-0-Unacademy-Logo-RGB.png" alt="Unacademy Logo" style={{ height: '60px' }} />
          <h2 className="mt-3">AHMEDABAD CENTRE</h2>
          <h4>🎓 Admin Learner Lookup</h4>
        </div>

        <Form className="d-flex justify-content-center mb-4">
          <Form.Control
            type="text"
            placeholder="Enter Roll Number"
            value={searchRollNo}
            onChange={(e) => setSearchRollNo(e.target.value)}
            className="me-3 w-50"
          />
          <Button variant="primary" onClick={handleSearch}>🔍 Search</Button>
        </Form>

        {loading ? (
          <div className="text-center">
            <Spinner animation="border" variant="primary" />
          </div>
        ) : filteredLearners.length > 0 ? (
          <div ref={reportRef} className="report-container">
            <h5 className="mb-3">Learner Information</h5>
            <Table bordered hover responsive size="sm" className="text-center">
              <thead className="table-primary">
                <tr>
                  <th>Learner Name</th>
                  <th>Batch Name</th>
                  <th>Roll No</th>
                  <th>Contact No</th>
                  <th>Session</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{filteredLearners[0]["Student Name"]}</td>
                  <td>{filteredLearners[0].Batch}</td>
                  <td>{filteredLearners[0].RollNo}</td>
                  <td>{filteredLearners[0]["Contact No"]}</td>
                  <td>2025-26</td>
                </tr>
              </tbody>
            </Table>

            <h5 className="mb-3">Attendance Report 2025-26</h5>
            <Table bordered hover responsive size="sm" className="text-center">
              <thead className="table-success">
                <tr>
                  <th>APR</th><th>MAY</th><th>JUN</th><th>JUL</th>
                  <th>AUG</th><th>SEP</th><th>OCT</th><th>NOV</th>
                  <th>DEC</th><th>JAN</th><th>FEB</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{filteredLearners[0].APR}</td>
                  <td>{filteredLearners[0].MAY}</td>
                  <td>{filteredLearners[0].JUN}</td>
                  <td>{filteredLearners[0].JUL}</td>
                  <td>{filteredLearners[0].AUG}</td>
                  <td>{filteredLearners[0].SEP}</td>
                  <td>{filteredLearners[0].OCT}</td>
                  <td>{filteredLearners[0].NOV}</td>
                  <td>{filteredLearners[0].DEC}</td>
                  <td>{filteredLearners[0].JAN}</td>
                  <td>{filteredLearners[0].FEB}</td>
                </tr>
              </tbody>
            </Table>

            <h5 className="mb-3">Learner Performance Report</h5>
            <Table bordered hover responsive size="sm" className="text-center">
              <thead className="table-warning">
                <tr>
                  <th>S.NO.</th>
                  <th>EXAM DATE</th>
                  <th>TEST NAME</th>
                  <th>PHY</th>
                  <th>CHEM</th>
                  <th>MATH</th>
                  <th>BIO</th>
                  <th>TOTAL</th>
                  <th>%AGE</th>
                  <th>RANK</th>
                  <th>MAX. MARKS</th>
                </tr>
              </thead>
              <tbody>
                {filteredLearners.map((learner, idx) => (
                  <tr key={idx}>
                    <td>{idx + 1}</td>
                    <td>{learner["Exam Date"]}</td>
                    <td>{learner["TestName"]}</td>
                    <td>{learner["Phy"]}</td>
                    <td>{learner["Chem"]}</td>
                    <td>{learner["Math"]}</td>
                    <td>{learner["Bio"]}</td>
                    <td>{learner["Total"]}</td>
                    <td>{learner["%AGE"]}</td>
                    <td>{learner["Rank"]}</td>
                    <td>{learner["Max. Marks"]}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        ) : (
          searchRollNo && <div className="text-center text-danger">No data found for Roll No: {searchRollNo}</div>
        )}

        <div className="text-center mt-4">
          <Button variant="outline-secondary" onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')}>
            {theme === 'light' ? '🌙 Dark Mode' : '🌞 Light Mode'}
          </Button>{' '}
          <Button variant="success" onClick={exportToPDF}>📄 Export PDF</Button>{' '}
          <Button variant="info" onClick={() => window.print()}>🖨️ Print Report</Button>{' '}
          <Button variant="secondary" onClick={() => navigate('/')}>🔙 Back</Button>
        </div>
      </Card>
    </Container>
  );
};

export default AdminDashboard;
